import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Container, Row, Col, Form, Button, Card, Alert } from "react-bootstrap";
import { FaGoogle, FaFacebook } from "react-icons/fa";

const SignupSection = () => {
  const [formData, setFormData] = useState({
    fullName: "",
    email: "",
    password: "",
  });
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1); // 1: Đăng ký, 2: Nhập OTP
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Registration failed");
      setStep(2); // Chuyển qua bước nhập OTP
    } catch (err) {
      setError(err.message);
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const response = await fetch("http://localhost:5000/api/verify-email", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email: formData.email, otp }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "OTP verification failed");
      navigate("/dashboard"); // Chuyển đến trang chính sau khi xác thực thành công
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <section className="d-flex justify-content-center align-items-center min-vh-100 bg-light">
      <Container>
        <Row className="justify-content-center">
          <Col md={5}>
            <Card className="p-4 shadow-sm border-0">
              {error && <Alert variant="danger">{error}</Alert>}
              {step === 1 ? (
                <>
                  <h3 className="text-center">Create an Account</h3>
                  <Form onSubmit={handleRegister}>
                    <Form.Group className="mb-3">
                      <Form.Label>Full Name</Form.Label>
                      <Form.Control type="text" name="fullName" value={formData.fullName} onChange={handleChange} required />
                    </Form.Group>
                    <Form.Group className="mb-3">
                      <Form.Label>Email</Form.Label>
                      <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} required />
                    </Form.Group>
                    <Form.Group className="mb-3">
                      <Form.Label>Password</Form.Label>
                      <Form.Control type="password" name="password" value={formData.password} onChange={handleChange} required />
                    </Form.Group>
                    <Button variant="dark" className="w-100" type="submit">Sign Up</Button>
                  </Form>
                </>
              ) : (
                <>
                  <h3 className="text-center">Verify Your Email</h3>
                  <p className="text-muted text-center">Enter the OTP sent to {formData.email}</p>
                  <Form onSubmit={handleVerifyOtp}>
                    <Form.Group className="mb-3">
                      <Form.Label>OTP Code</Form.Label>
                      <Form.Control type="text" value={otp} onChange={(e) => setOtp(e.target.value)} required />
                    </Form.Group>
                    <Button variant="dark" className="w-100" type="submit">Verify OTP</Button>
                  </Form>
                </>
              )}
            </Card>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default SignupSection;
